mapper.select:
SELECT id, type_code, type_name, description, icon, sort_order, is_active, create_time, update_time
FROM question_types
WHERE id = #{id}

SELECT id, type_code, type_name, description, icon, sort_order, is_active, create_time, update_time
FROM question_types
WHERE type_code = #{typeCode}

mapper.update:
UPDATE question_types
type_code = #{typeCode}, type_name = #{typeName}, description = #{description}, icon = #{icon}, sort_order = #{sortOrder}, is_active = #{isActive}, update_time = NOW()
WHERE id = #{id}
